mapper.insert:

insert into exam_subject (id, industry, work_type, 
  qualified_score, total_score, exam_time, 
  operate_time, operator, operator_ip
  )
values (#{id,jdbcType=INTEGER}, #{industry,jdbcType=INTEGER}, #{workType,jdbcType=INTEGER}, 
  #{qualifiedScore,jdbcType=REAL}, #{totalScore,jdbcType=REAL}, #{examTime,jdbcType=INTEGER}, 
  #{operateTime,jdbcType=TIMESTAMP}, #{operator,jdbcType=VARCHAR}, #{operatorIp,jdbcType=VARCHAR}
  )

mapper.select:
SELECT count(1)
FROM exam_subject
WHERE industry = #{industry}
  AND work_type = #{workType}
